SELECT person.la_person_id, person.person_ethnicity, person.person_upn, person.person_upn_unknown, person.person_send, person.person_expected_dob, person.person_death_date, person.person_is_mother, person.person_nationality, family.family_id, family.la_person_id, address.address, address.la_person_id, address.address_type, address.address_start, address.address_end, address.address_postcode, disability.la_person_id, disability.person_disability FROM person INNER JOIN family ON person.la_person_id = family.la_person_id INNER JOIN address ON person.la_person_id = address.la_person_id INNER JOIN disability ON person.la_person_id = disability.la_person_id WHERE entry_date >= ADD_MONTHS(SYSDATE, -6*12);