USE PLACEHOLDER_DB_NAME;
SELECT person_knowsley.la_person_id, person_knowsley.person_sex_knowsley, person_knowsley.person_gender_knowsley, person_knowsley.person_ethnicity, person_knowsley.person_dob, person_knowsley.person_upn, person_knowsley.person_upn_unknown, person_knowsley.person_send, person_knowsley.person_expected_dob, person_knowsley.person_death_date, person_knowsley.person_is_mother, person_knowsley.person_nationality, family.family_id, family.la_person_id, address_knowsley.address, address_knowsley.la_person_id, address_knowsley.address_type_knowsley, address_knowsley.address_start, address_knowsley.address_end, address_knowsley.address_postcode_knowsley, disability.la_person_id, disability.person_disability FROM person INNER JOIN family ON person.la_person_id = family.la_person_id INNER JOIN address ON person.la_person_id = address.la_person_id INNER JOIN disability ON person.la_person_id = disability.la_person_id WHERE entry_date >= DATEADD(YEAR, -6, GETDATE());