USE PLACEHOLDER_DB_NAME;
SELECT person_details.la_person_id, person_details.person_details_ethnicity, person_details.person_details_upn, person_details.person_details_upn_unknown, person_details.person_details_send, person_details.person_details_expected_dob, person_details.person_details_death_date, person_details.person_details_is_mother, person_details.person_details_nationality, family.family_id, family.la_person_id, address.address, address.la_person_id, address.address_type, address.address_start, address.address_end, address.address_postcode, disability.la_person_id, disability.person_disability FROM person INNER JOIN family ON person.la_person_id = family.la_person_id INNER JOIN address ON person.la_person_id = address.la_person_id INNER JOIN disability ON person.la_person_id = disability.la_person_id WHERE entry_date >= DATEADD(YEAR, -6, GETDATE());